!(function (e) {
  "use strict";

  // Throttle function
  function throttle(func, limit) {
    var inThrottle;
    return function () {
      var args = arguments,
        context = this;
      if (!inThrottle) {
        func.apply(context, args);
        inThrottle = true;
        setTimeout(function () {
          inThrottle = false;
        }, limit);
      }
    };
  }

  e('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function () {
    if (
      location.pathname.replace(/^\//, "") ==
        this.pathname.replace(/^\//, "") &&
      location.hostname == this.hostname
    ) {
      var a = e(this.hash);
      if ((a = a.length ? a : e("[name=" + this.hash.slice(1) + "]")).length)
        return (
          e("html, body").animate(
            { scrollTop: a.offset().top - 54 },
            1e3,
            "easeInOutExpo"
          ),
          !1
        );
    }
  });

  e(".js-scroll-trigger").click(function () {
    e(".navbar-collapse").collapse("hide");
  });

  e("body").scrollspy({ target: "#mainNav", offset: 56 });

  function a() {
    100 < e("#mainNav").offset().top
      ? e("#mainNav").addClass("navbar-shrink")
      : e("#mainNav").removeClass("navbar-shrink");
  }

  // Use the throttled function for the scroll event
  a();
  e(window).scroll(throttle(a, 100)); // Throttle to run at most every 100ms
})(jQuery);
